try {
  const tallyData = $input.first().json;
  const snovData = $input.all()[1]?.json || {};
  const apolloData = $input.all()[2]?.json || {};

  // Combine enrichment data
  const enrichedLead = {
    timestamp: new Date().toISOString(),
    source: 'tally_form',
    email: tallyData.email || '',
    name: tallyData.name || snovData.name || apolloData.name || '',
    company: snovData.company || apolloData.company || '',
    position: snovData.position || apolloData.title || '',
    linkedin: snovData.linkedin || apolloData.linkedin_url || '',
    phone: snovData.phone || apolloData.phone || '',
    location: snovData.location || apolloData.location || '',
    interests: tallyData.interests || [],
    dream_car_budget: tallyData.dream_car_budget || 'ab 99‚Ç¨',
    income_goal: tallyData.income_goal || 'passives Einkommen',
    personality_traits: [],
    enrichment_score: (snovData.confidence || 0) + (apolloData.score || 0),
    holographic_profile: {
      avatar_style: 'crystal_glass_lion',
      voice_persona: tallyData.preferred_voice || 'Lina',
      emotion_profile: 'enthusiastic_freedom_seeker',
      interaction_style: 'roar_some_motivational'
    }
  };

  // Determine personality traits based on form responses
  if (tallyData.motivation?.includes('freedom')) {
    enrichedLead.personality_traits.push('freedom_seeker');
  }
  if (tallyData.motivation?.includes('money')) {
    enrichedLead.personality_traits.push('wealth_builder');
  }
  if (tallyData.motivation?.includes('time')) {
    enrichedLead.personality_traits.push('time_optimizer');
  }
  if (tallyData.interests?.includes('cars')) {
    enrichedLead.personality_traits.push('car_enthusiast');
  }

  return [enrichedLead];
} catch (error) {
  console.error('Holographic Projector Error:', error);
  return [{
    error: error.message,
    timestamp: new Date().toISOString(),
    status: 'enrichment_failed'
  }];
}

