// GENETIC OPTIMIZATION ENGINE - ALIEN DNA EVOLUTION
const contentData = $json;
const viralPatterns = contentData.viral_patterns || [];
const trendData = contentData.trend_analysis || {};

// Genetic Algorithm for Content Evolution
class GeneticContentOptimizer {
  constructor() {
    this.population_size = 100;
    this.mutation_rate = 0.15;
    this.crossover_rate = 0.8;
    this.elite_size = 10;
    this.generations = 50;
  }

  // Create initial population of content variants
  createInitialPopulation(baseContent) {
    const population = [];
    const variations = {
      hooks: [
        'Crystal-LÃ¶we explodiert aus Quantum-Portal',
        'Glas-DNA mutiert zu Reichtum',
        'VSMR-Hypnose aktiviert Erfolgs-Gene',
        'Hologramm-RealitÃ¤t materialisiert TrÃ¤ume',
        'Alien-Technologie entschlÃ¼sselt Freiheit'
      ],
      emotions: ['excitement', 'curiosity', 'urgency', 'desire', 'fear_of_missing_out'],
      call_to_actions: [
        'Starte JETZT deine Galaxy-Eroberung!',
        'Aktiviere deine Crystal-LÃ¶we-Power!',
        'Betritt das Hologramm-Portal!',
        'Lass dich von Alien-Tech transformieren!',
        'Werde zum Galaktischen Herrscher!'
      ],
      visual_elements: ['crystal_lion', 'glass_transformation', 'quantum_portal', 'hologram', 'alien_tech'],
      frequencies: [432, 528, 741, 963, 40]
    };

    for (let i = 0; i < this.population_size; i++) {
      const individual = {
        id: crypto.randomUUID(),
        hook: variations.hooks[Math.floor(Math.random() * variations.hooks.length)],
        emotion: variations.emotions[Math.floor(Math.random() * variations.emotions.length)],
        cta: variations.call_to_actions[Math.floor(Math.random() * variations.call_to_actions.length)],
        visual: variations.visual_elements[Math.floor(Math.random() * variations.visual_elements.length)],
        frequency: variations.frequencies[Math.floor(Math.random() * variations.frequencies.length)],
        fitness: 0,
        viral_score: 0
      };
      population.push(individual);
    }

    return population;
  }

  // Calculate fitness based on viral potential
  calculateFitness(individual, trendData) {
    let fitness = 0;

    // Hook effectiveness (0-30 points)
    if (individual.hook.includes('Crystal-LÃ¶we')) fitness += 10;
    if (individual.hook.includes('Quantum') || individual.hook.includes('Alien')) fitness += 8;
    if (individual.hook.includes('explodiert') || individual.hook.includes('mutiert')) fitness += 7;
    if (individual.hook.includes('VSMR') || individual.hook.includes('Hologramm')) fitness += 5;

    // Emotion alignment (0-25 points)
    const emotionScores = {
      'excitement': 25, 'curiosity': 22, 'urgency': 20, 
      'desire': 18, 'fear_of_missing_out': 15
    };
    fitness += emotionScores[individual.emotion] || 0;

    // CTA strength (0-20 points)
    if (individual.cta.includes('JETZT')) fitness += 8;
    if (individual.cta.includes('Galaxy') || individual.cta.includes('Crystal-LÃ¶we')) fitness += 7;
    if (individual.cta.includes('!')) fitness += 5;

    // Visual element power (0-15 points)
    const visualScores = {
      'crystal_lion': 15, 'quantum_portal': 12, 'alien_tech': 10,
      'hologram': 8, 'glass_transformation': 6
    };
    fitness += visualScores[individual.visual] || 0;

    // Frequency resonance (0-10 points)
    const frequencyScores = { 432: 10, 528: 8, 741: 6, 963: 4, 40: 2 };
    fitness += frequencyScores[individual.frequency] || 0;

    // Trend alignment bonus (0-20 points)
    if (trendData.trending_keywords) {
      trendData.trending_keywords.forEach(keyword => {
        if (individual.hook.toLowerCase().includes(keyword.toLowerCase())) {
          fitness += 5;
        }
      });
    }

    individual.fitness = fitness;
    individual.viral_score = (fitness / 120) * 100; // Convert to percentage

    return individual;
  }

  // Crossover two individuals
  crossover(parent1, parent2) {
    if (Math.random() > this.crossover_rate) return [parent1, parent2];

    const child1 = {
      id: crypto.randomUUID(),
      hook: Math.random() < 0.5 ? parent1.hook : parent2.hook,
      emotion: Math.random() < 0.5 ? parent1.emotion : parent2.emotion,
      cta: Math.random() < 0.5 ? parent1.cta : parent2.cta,
      visual: Math.random() < 0.5 ? parent1.visual : parent2.visual,
      frequency: Math.random() < 0.5 ? parent1.frequency : parent2.frequency,
      fitness: 0,
      viral_score: 0
    };

    const child2 = {
      id: crypto.randomUUID(),
      hook: child1.hook === parent1.hook ? parent2.hook : parent1.hook,
      emotion: child1.emotion === parent1.emotion ? parent2.emotion : parent1.emotion,
      cta: child1.cta === parent1.cta ? parent2.cta : parent1.cta,
      visual: child1.visual === parent1.visual ? parent2.visual : parent1.visual,
      frequency: child1.frequency === parent1.frequency ? parent2.frequency : parent1.frequency,
      fitness: 0,
      viral_score: 0
    };

    return [child1, child2];
  }

  // Mutate individual
  mutate(individual) {
    if (Math.random() > this.mutation_rate) return individual;

    const mutations = {
      hook: [
        'Quantum-Crystal-LÃ¶we durchbricht RealitÃ¤t',
        'Alien-Glas-DNA aktiviert Reichtums-Code',
        'VSMR-Portal Ã¶ffnet Erfolgs-Dimension',
        'Hologramm-LÃ¶we fÃ¼hrt zur Galaxy-Herrschaft'
      ],
      emotion: ['transcendence', 'cosmic_awakening', 'quantum_excitement'],
      cta: [
        'Erobere JETZT das Universum!',
        'Aktiviere deine Alien-DNA!',
        'Betritt die Erfolgs-Galaxy!'
      ]
    };

    const mutationType = Math.random();
    if (mutationType < 0.3 && mutations.hook.length > 0) {
      individual.hook = mutations.hook[Math.floor(Math.random() * mutations.hook.length)];
    } else if (mutationType < 0.6 && mutations.emotion.length > 0) {
      individual.emotion = mutations.emotion[Math.floor(Math.random() * mutations.emotion.length)];
    } else if (mutations.cta.length > 0) {
      individual.cta = mutations.cta[Math.floor(Math.random() * mutations.cta.length)];
    }

    return individual;
  }

  // Run genetic algorithm
  evolve(baseContent, trendData) {
    let population = this.createInitialPopulation(baseContent);
    let bestIndividual = null;
    let generationStats = [];

    for (let generation = 0; generation < this.generations; generation++) {
      // Calculate fitness for all individuals
      population = population.map(individual => 
        this.calculateFitness(individual, trendData)
      );

      // Sort by fitness
      population.sort((a, b) => b.fitness - a.fitness);

      // Track best individual
      if (!bestIndividual || population[0].fitness > bestIndividual.fitness) {
        bestIndividual = { ...population[0] };
      }

      // Generation statistics
      const avgFitness = population.reduce((sum, ind) => sum + ind.fitness, 0) / population.length;
      generationStats.push({
        generation: generation,
        best_fitness: population[0].fitness,
        avg_fitness: avgFitness,
        best_viral_score: population[0].viral_score
      });

      // Create next generation
      const newPopulation = [];

      // Keep elite
      for (let i = 0; i < this.elite_size; i++) {
        newPopulation.push({ ...population[i] });
      }

      // Generate offspring
      while (newPopulation.length < this.population_size) {
        // Tournament selection
        const parent1 = this.tournamentSelection(population);
        const parent2 = this.tournamentSelection(population);

        // Crossover
        const [child1, child2] = this.crossover(parent1, parent2);

        // Mutation
        newPopulation.push(this.mutate(child1));
        if (newPopulation.length < this.population_size) {
          newPopulation.push(this.mutate(child2));
        }
      }

      population = newPopulation;
    }

    return {
      best_individual: bestIndividual,
      generation_stats: generationStats,
      final_population: population.slice(0, 10), // Top 10
      optimization_complete: true,
      alien_evolution_level: 'MAXIMUM'
    };
  }

  // Tournament selection
  tournamentSelection(population, tournamentSize = 5) {
    const tournament = [];
    for (let i = 0; i < tournamentSize; i++) {
      const randomIndex = Math.floor(Math.random() * population.length);
      tournament.push(population[randomIndex]);
    }
    tournament.sort((a, b) => b.fitness - a.fitness);
    return tournament[0];
  }
}

// Initialize and run genetic optimization
const optimizer = new GeneticContentOptimizer();
const evolutionResult = optimizer.evolve(contentData, trendData);

return {
  genetic_optimization: evolutionResult,
  optimized_content: evolutionResult.best_individual,
  viral_score: evolutionResult.best_individual?.viral_score || 0,
  evolution_complete: true,
  alien_tech_level: 'YEAR_3025_GENETIC_MAXIMUM',
  next_phase: 'a_b_testing'
};

// VIRAL THRESHOLD GATE - GALAXY CONQUEST FILTER
const claudeResults = $('claude-opus-41-testing-057').first().json;
const deepseekResults = $('deepseek-r1-scorer-058').first().json;
const geneticResults = $('genetic-optimizer-056').first().json;

// Extract viral scores from DeepSeek analysis
let viralScores = [];
try {
  const deepseekContent = deepseekResults.choices?.[0]?.message?.content || '';
  const scoreMatches = deepseekContent.match(/Viral Probability[:\s]*([0-9.]+)%/gi) || [];
  viralScores = scoreMatches.map(match => {
    const score = parseFloat(match.match(/([0-9.]+)/)[1]);
    return score;
  });
} catch (error) {
  console.log('Error parsing viral scores:', error);
}

// Galaxy Conquest Threshold
const GALAXY_THRESHOLD = 97.3;
const BILLION_VIEW_THRESHOLD = 95.0;
const ALIEN_TECH_THRESHOLD = 90.0;

// Analyze each variant
const variantAnalysis = {
  variant_a: {
    name: 'Crystal-LÃ¶we Explosion',
    viral_score: viralScores[0] || 0,
    galaxy_conquest_ready: (viralScores[0] || 0) >= GALAXY_THRESHOLD,
    billion_view_potential: (viralScores[0] || 0) >= BILLION_VIEW_THRESHOLD,
    alien_tech_level: 'MAXIMUM'
  },
  variant_b: {
    name: 'Glas-DNA Transformation',
    viral_score: viralScores[1] || 0,
    galaxy_conquest_ready: (viralScores[1] || 0) >= GALAXY_THRESHOLD,
    billion_view_potential: (viralScores[1] || 0) >= BILLION_VIEW_THRESHOLD,
    alien_tech_level: 'ULTRA'
  },
  variant_c: {
    name: 'VSMR Hypnose',
    viral_score: viralScores[2] || 0,
    galaxy_conquest_ready: (viralScores[2] || 0) >= GALAXY_THRESHOLD,
    billion_view_potential: (viralScores[2] || 0) >= BILLION_VIEW_THRESHOLD,
    alien_tech_level: 'SUPREME'
  },
  variant_d: {
    name: 'Hologramm-Portal',
    viral_score: viralScores[3] || 0,
    galaxy_conquest_ready: (viralScores[3] || 0) >= GALAXY_THRESHOLD,
    billion_view_potential: (viralScores[3] || 0) >= BILLION_VIEW_THRESHOLD,
    alien_tech_level: 'ADVANCED'
  },
  variant_e: {
    name: 'Alien-Tech Freiheit',
    viral_score: viralScores[4] || 0,
    galaxy_conquest_ready: (viralScores[4] || 0) >= GALAXY_THRESHOLD,
    billion_view_potential: (viralScores[4] || 0) >= BILLION_VIEW_THRESHOLD,
    alien_tech_level: 'MAXIMUM'
  }
};

