  const indicators = {
    wealth_seeker: (lead.income_level || 0) > 50000 || lead.job_title?.includes('CEO') || lead.job_title?.includes('Founder'),
    car_enthusiast: lead.interests?.includes('cars') || lead.social_posts?.some(p => p.includes('car')),
    freedom_lover: lead.bio?.includes('freedom') || lead.interests?.includes('travel'),
    team_builder: lead.connections > 500 || lead.job_title?.includes('Manager'),
    tech_savvy: lead.skills?.includes('technology') || lead.industry === 'tech',
    luxury_oriented: lead.spending_patterns?.luxury > 0.3,
    success_driven: lead.achievements?.length > 3,
    social_influencer: lead.followers > 1000
  };

  return indicators;
}

// Avatar matching algorithm
function matchAvatar(personality, avatars) {
  const scores = avatars.map(avatar => {
    let score = 0;

    // Personality matching
    if (personality.wealth_seeker && avatar.specialty.includes('Reichtum')) score += 10;
    if (personality.car_enthusiast && avatar.specialty.includes('Traumauto')) score += 10;
    if (personality.freedom_lover && avatar.specialty.includes('Freiheit')) score += 10;
    if (personality.team_builder && avatar.specialty.includes('Team')) score += 10;
    if (personality.tech_savvy && avatar.galaxy_power.includes('Quantum')) score += 8;
    if (personality.luxury_oriented && avatar.role.includes('Luxury')) score += 8;
    if (personality.success_driven && avatar.role.includes('Success')) score += 8;
    if (personality.social_influencer && avatar.role.includes('Community')) score += 8;

    // Random factor for variety
    score += Math.random() * 5;

    return { ...avatar, match_score: score };
  });

  return scores.sort((a, b) => b.match_score - a.match_score)[0];
}

