  recraft_vectors: $node['recraft_vector_007'].json?.data?.[0]?.url ? [$node['recraft_vector_007'].json.data[0].url] : []
};

// LR Products for Galaxy Conquest (>99‚Ç¨ filter)
const galaxyProducts = [
  {
    name: 'LR Lifetakt Galaxy Edition',
    price: 149.90,
    category: 'Health & Wellness',
    description: 'Quantum-enhanced health monitoring with crystal frequency alignment',
    visual_concept: 'Crystal health device floating in galaxy portal',
