    viral_potential: 97.8
  },
  {
    name: 'Aloe Vera Gel Crystal Healing',
    price: 129.90,
    category: 'Skincare',
    description: 'Quantum-infused aloe vera with 432Hz frequency activation',
    visual_concept: 'Aloe plant transforming into liquid crystal',
    target_audience: 'Wellness enthusiasts',
