    visual_concept: 'Nutrition particles forming galaxy constellation',
    target_audience: 'Fitness and health optimizers',
    viral_potential: 95.7
  },
  {
    name: 'ProBalance Universal Harmony',
    price: 159.90,
    category: 'Wellness',
    description: 'Complete wellness system with holographic balance tracking',
    visual_concept: 'Balance scales floating in crystal dimension',
    target_audience: 'Holistic wellness seekers',
    viral_potential: 94.9
  }
];

// Asset mapping for each product
const assetMapping = galaxyProducts.map((product, index) => {
  return {
    product: product,
    visual_assets: {
      base_image: visualAssets.leonardo_images[index % visualAssets.leonardo_images.length],
      transparent_version: visualAssets.removed_bg_images[index % visualAssets.removed_bg_images.length],
      3d_model: visualAssets.tripo3d_models[index % visualAssets.tripo3d_models.length],
      glass_render: visualAssets.flux_glass_renders[index % visualAssets.flux_glass_renders.length],
