});

// Generate dynamic visual concepts
const dynamicConcepts = assetMapping.map(item => {
  return {
    product_name: item.product.name,
    price: item.product.price,
    concept: item.product.visual_concept,
    assets: item.visual_assets,
    viral_score: item.product.viral_potential,
    ready_for_video: true
  };
});

return {
  total_products: galaxyProducts.length,
  selected_products: galaxyProducts.filter(p => p.price >= 99),
  asset_mapping: assetMapping,
  dynamic_concepts: dynamicConcepts,
